import React, { useState } from "react";
import { AiOutlineCheck } from "react-icons/ai";

import "./CourseGoalItem.css";

const CourseGoalItem = (props) => {
  // const [deleteText, setDeleteText] = useState('');
  let [clicked, setClicked] = useState("");
  const deleteHandler = () => {
    setClicked("clicked");
    setTimeout(() => {
      props.onDelete(props.id);
    }, 250);
    // setDeleteText('(Deleted!)');
    const [courseGoals, setCourseGoals] = useState([]);

    useEffect(() => {
      const storedGoals = JSON.parse(localStorage.getItem("courseGoals"));
      if (storedGoals) {
        setCourseGoals(storedGoals);
      }
    }, []);

    useEffect(() => {
      localStorage.setItem("courseGoals", JSON.stringify(courseGoals));
    }, [courseGoals]);

    const addGoalHandler = (enteredText) => {
      setCourseGoals((prevGoals) => {
        const updatedGoals = [...prevGoals];
        updatedGoals.unshift({
          text: enteredText,
          id: Math.random().toString(),
        });
        return updatedGoals;
      });
    };

    const deleteItemHandler = (goalId) => {
      setCourseGoals((prevGoals) => {
        const updatedGoals = prevGoals.filter((goal) => goal.id !== goalId);
        return updatedGoals;
      });
    };

    let content = (
      <p style={{ textAlign: "center" }}>No goals found. Maybe add one?</p>
    );

    if (courseGoals.length > 0) {
      content = (
        <CourseGoalList items={courseGoals} onDeleteItem={deleteItemHandler}>
          {" "}
        </CourseGoalList>
      );
    }

    return (
      <div className="container">
        <section id="goal-form">
          <CourseInput onAddGoal={addGoalHandler} />
        </section>
        <section id="goals">
          {content}
          {/* {courseGoals.length > 0 && (
          <CourseGoalList
            items={courseGoals}
            onDeleteItem={deleteItemHandler}
          />
        ) // <p style={{ textAlign: 'center' }}>No goals found. Maybe add one?</p>
        } */}
        </section>
      </div>
    );
  };

  return (
    <li className={`goal-item ${clicked}`}>
      <AiOutlineCheck onClick={deleteHandler} className={`icon ${clicked}`} />
      {props.children}
    </li>
  );
};

export default CourseGoalItem;
