import React, { useState } from "react";
import { AiOutlineCheck } from "react-icons/ai";
import CourseInput from "../CourseInput/CourseInput";
import CourseInputInner from "../CourseInput/CourseInputInner";

import "./CourseGoalItem.css";

const CourseGoalItem = (props) => {
  // const [deleteText, setDeleteText] = useState('');
  let [clicked, setClicked] = useState("");
  const deleteHandler = () => {
    setClicked("clicked");
    setTimeout(() => {
      props.onDelete(props.id);
    }, 250);
    // setDeleteText('(Deleted!)');
  };

  return (
    <li className={`goal-item ${clicked}`}>
      {props.children}
      <AiOutlineCheck onClick={deleteHandler} className={`icon ${clicked}`} />
      <CourseInputInner></CourseInputInner>
      {props.children}
    </li>
  );
};

export default CourseGoalItem;
